import React, { useCallback, useState } from 'react';
import Head from 'next/head';
import {Form, Input , Checkbox, Button} from 'antd';
import styled from 'styled-components';

import AppLayout from '../components/AppLayout';
import useInput from '../hooks/userInput';
import Password from 'antd/lib/input/Password';

const ErroMessege = styled.div`
	color: red;
`;

const Signup = () => {
	const [id, onChangeId] = useInput('');
	const [nickname, onChangeNickname] = useInput("");
	const [password, onChangePassword] = useInput("");
	const [passwordCheck, setPasswordCheck] = useState ('');
	const [passwordError, setPasswordError] = useState (false);

	const onChangePasswordCheck = useCallback(()=> {
		setPasswordCheck(e.target.value);
		setPasswordError(e.target.value !== password);
	}, [password]);

	const [term, setTerm] = useState ('');	
	const [termError, setTermError] = useState ('');	
	const onChangeTerm = useCallback(()=> {
		setTerm(e.target.checked);
		setTermError(false);
	}, []);
	const onSubmit = useCallback(()=> {
		if(password !== passwordCheck) {
			return setPasswordError(true);
		}
		if(!term) {
			return setTermError(true);
		}
		console.log(id, nickname, password);
	},[password, passwordCheck, term])
    return (
        <AppLayout>
        <Head>
            <meta charSet='utf-8' />
			<title>회원가입 | NodeBird</title>
        </Head>
        <Form onFinish={onSubmit}> 
            <div>
              <label htmlFor="user-id">아이디</label>
							<br/>
							<Input name="user-id" value={id} required onChange={onChangeId}></Input>
            </div>
						<div>
              <label htmlFor="user-nick">닉네임</label>
							<br/>
							<Input name="user-nick" value={nickname} required onChange={onChangeNickname}></Input>
            </div>
						<div>
              <label htmlFor="user-password">비밀번호</label>
							<br/>
							<Input name="user-password" value={password} required onChange={onChangePassword}></Input>
            </div>
						<div>
              <label htmlFor="user-password">비밀번호 체크</label>
							<br/>
							<Input name="user-password-check" value={passwordCheck} required onChange={onChangePasswordCheck}></Input>
							{passwordError && <ErroMessege>비밀번호가 일치하지 않습니다.</ErroMessege>}
						</div>
						<div>
							<Checkbox name="user-term" checked={term} onChange={onChangeTerm}>
									개인정보 이용약관 동의합니다.
							</Checkbox>
							{termError && <ErroMessege>약관에 동의하셔야합니다.</ErroMessege>}
						</div>
						<div style={{marginTop:10}}>
							<button type="primary" htmlType="submit"></button>
						</div>
        </Form>
        </AppLayout>
    )

};

export default Signup;
